import { useEffect } from 'react';
import {
  createElement,
  loadAirwallex,
  getElement,
  confirmPaymentIntent,
  ElementType,
} from 'airwallex-payment-elements';

const intent_id = 'int_n10tqMpQzvI20pwBQzIACXXqUZo';
const client_secret = 'eyJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MDkzOTI4MDcsImV4cCI6MTYwOTM5NjQwNywiYWNjb3VudF9pZCI6IjZkMzljOGU3LWQyY2EtNGQ0Yy04N2I5LWIzY2Y3MzNkNTU2ZSIsImRhdGFfY2VudGVyX3JlZ2lvbiI6IlVLIiwiaW50ZW50X2lkIjoiaW50X24xMHRxTXBRenZJMjBwd0JReklBQ1hYcVVabyJ9.yszCObRTB-dcyWFVXBb4GqrVgVqKAYH5Cah3a7vsqxI';
const ELEMENT_TYPE: ElementType = 'card';

function card() {
  useEffect(() => {
    loadAirwallex({
      env: 'staging',
      origin: window.location.origin,
    }).then(() => {
      createElement(ELEMENT_TYPE)?.mount(ELEMENT_TYPE);
    });

    const onReady = (event: CustomEvent) => {
      /*
      ... Handle event
    */
      console.log(`Elements ready with ${JSON.stringify(event.detail)}`);
    };

    window.addEventListener('onReady', onReady as EventListener);
    return () => {
      window.removeEventListener('onReady', onReady as EventListener);
    };
  });

  const triggerConfirm = async () => {
    try {
      const cardNumberElement = getElement('card');
      if (cardNumberElement) {
        const confirmResult = await confirmPaymentIntent({
          element: cardNumberElement,
          id: intent_id,
          client_secret,
          payment_method_options: {
            card: {
              auto_capture: true,
            },
          },
        });
        console.log(`confirm success with ${JSON.stringify(confirmResult)}`);
      }
    } catch (err) {
      console.log(`confirm fail with ${JSON.stringify(err)}`);
    }
  };

  return (
    <div>
      <h2>Option #6: Card element integration</h2>
      <div id={ELEMENT_TYPE} />
      <button onClick={triggerConfirm} style={{ marginTop: '8px' }}>
        Confirm
      </button>
    </div>
  );
}

export default card;
